import React from 'react';
import axios from 'axios';
import { fstat, writeFile } from 'fs';

class sub extends React.Component{

  constructor(props) {
	super(props);
	this.state = {
  	name: '',
  	email: '',
  	message: ''
	}
  }
  handleSubmit(e) {
    e.preventDefault();

    axios({
		method: "POST", 
		url:"https://mailthis.to/jyao413@aucklanduni.ac.nz", 
		data:  this.state
	  })
	console.log(this.state)
	this.resetForm()

  }
  resetForm(){

     this.setState({name: '', email: '', message: ''})
  }

  render() {
	return(
		<form action="https://mailthis.to/tigerj20"
		method="POST" encType="multipart/form-data">
		<input type="text" name="name" placeholder="Your name"/>
		<input type="email" name="_replyto" placeholder="Your email"/>
		<textarea name="message" placeholder="Enter your message here"></textarea>
		<input type="file" name="file" placeholder="Attachments (optional)"/>
		<input type="hidden" name="_subject" value="Contact form submitted"/>
		<input type="hidden" name="_after" value="https://myhomepage.net/"/>
		<input type="hidden" name="_honeypot" value=""/>
		<input type="hidden" name="_confirmation" value=""/>
		<input type="submit" value="Send"/>
		</form>

	);
  }

  onNameChange(event) {
	this.setState({name: event.target.value})
  }

  onEmailChange(event) {
	this.setState({email: event.target.value})
  }

  onMessageChange(event) {
	this.setState({message: event.target.value})
  }
}

export default sub;
